// SubmissionForm.js
import React, { useState } from 'react';
import axios from 'axios';

function SubmissionForm() {
  const [formData, setFormData] = useState({
    username: '',
    language: '',
    stdin: '',
    code: ''
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/submit', formData);
      alert('Snippet submitted successfully');
      setFormData({
        username: '',
        language: '',
        stdin: '',
        code: ''
      });
    } catch (error) {
      console.error('Error submitting snippet:', error);
      alert('Failed to submit snippet');
    }
  };

  return (
    <div>
      <h1>Submission Form</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" value={formData.username} onChange={handleChange} placeholder="Username" required /><br />
        <input type="text" name="language" value={formData.language} onChange={handleChange} placeholder="Language" required /><br />
        <input type="text" name="stdin" value={formData.stdin} onChange={handleChange} placeholder="Standard Input" required /><br />
        <textarea name="code" value={formData.code} onChange={handleChange} placeholder="Source Code" required /><br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default SubmissionForm;
